import React from "react";

class EditFishForm extends React.Component {
  render() {
    return (
      <div className="fish-edit">
        <input name="name" value={this.props.fish.name} type="text" />
        <input name="price" value={this.props.fish.price} type="text" />
        <select name="status" value={this.props.fish.status}>
          <option value="available">Fresh!</option>
          <option value="unavailable">Sold Out!</option>
        </select>
        <textarea name="desc" value={this.props.fish.desc} />
        <input name="image" type="text" value={this.props.fish.image} />
      </div>
    );
  }
}
export default EditFishForm;
